pragma solidity ^0.5.8;

import "./utils/SafeMath.sol";
import "./interfaces/IColdStakingVoting.sol";
import "./utils/Address.sol";

contract TreasuryGovernance {

    // TODO: Update calculations with SafeMath functions.

    using SafeMath for uint;
    using Address for address;
    using Address for address payable;

    struct Proposal
    {
        // Based on IOHK Treasury proposal system.
        string  name;
        string  URL;
        bytes32 hash;        // Hash of the proposal description text,
                             // serves for proposal integrity verification.
        uint    start_epoch; // Number of the epoch when proposal voting begins.
        uint    end_epoch;   // Number of the epoch when proposal funding ends.
        // NOTE: If a proposal is intended to be a one-time payment then 
        //       it must have `start_epoch` = `i` and `end_epoch` = `i + 1` because
        //       the proposal will be paid in the next epoch after the voting epoch ends.

        address payable payment_address;
        uint    payment_amount; // Amount of payment per epoch.
        // NOTE: If a proposal is intended to be paid in multiple epochs then
        //       it will reveive (`payment_amount` * epochs count) funds.
        uint    last_funded_epoch; // Number of the epoch in which the proposal was last funded.

        uint votes_for;
        uint votes_against;
        uint votes_abstain;

        uint status;

        // STATUS:
        // 0 - voting
        // 1 - accepted/ awaiting payment
        // 2 - declined
        // 3 - withdrawn

        // Collateral tx id is not necessary.
        // Proposal sublission tx requires `proposal_threshold` to be paid.
    }

    struct Vote
    {
        uint    vote_code; // 0 - for, 1 - against, 2 - abstain
        uint    weight;
    }

    // ------------------------------------------------------------ //

    IColdStakingVoting public cold_staking_contract; // Staking contract address and ABI.

    function setColdStakingContract(address _cold_staking_contract) public {
      cold_staking_contract = IColdStakingVoting(_cold_staking_contract);
    }


    uint public epoch_length        = 27 days; // Voting epoch length.
    uint public start_timestamp     = now;

    uint public total_voting_weight = 0; // This variable preserves the total amount of staked funds which participate in voting.
    uint public proposal_threshold  = 500 ether; // The amount of funds that will be held by voting contract during the proposal consideration/voting.
    uint public voting_threshold    = 50; // Percentage of votes which is required to consider that there are enough votes for the proposal. 
                                          // If a proposal was not voted enough then it will be rejected automatically.

    mapping(address => uint)     public voting_weight; // Each voters weight. Calculated and updated on each Cold Staked deposit change.
    mapping(bytes32 => Proposal) public proposals; // Use `bytes32` keccak256 hash of proposal name to identify a proposal entity.
    mapping(bytes32 => mapping(address => Vote))     public votes;
    mapping(uint    => bytes32)  public proposal_names;

    uint public last_proposal = 0;


    // Cold Staker can become a voter by executing this funcion.
    // Voting contract must read the weight of the staker
    // and update the total voting weight.
    function become_voter() public
    {
        require(voting_weight[msg.sender] == 0);
        uint _amount = cold_staking_contract.getStake(msg.sender);
        voting_weight[msg.sender] = _amount;
        total_voting_weight += _amount;
    }

    // Voter can resign from his voting rights.
    // In this case, his voting weight will be subtracted from total voting weight.
    // This person can become voter again by calling the `become_voter` function.
    function resign_voter() public
    {
        require(voting_weight[msg.sender] != 0);

        total_voting_weight -= voting_weight[msg.sender];
        voting_weight[msg.sender] = 0;
    }


    // Staking Contract MUST call this function on each staking deposit update (on withdrawals and deposits).
    function update_voter(address _who, uint _new_weight) public only_staking_contract()
    {
        // If the voting weight of a given address decreases
        // subtract the delta from `total_voting_weight`.
        if(voting_weight[_who] > _new_weight)
        {
            total_voting_weight -= (voting_weight[_who] - _new_weight);
        }

        // Otherwise the weight did not change or increases
        // we need to increase the total_voting_weight by delta.
        else
        {
            total_voting_weight += (_new_weight - voting_weight[_who]);
        }
        voting_weight[_who] = _new_weight;
    }

    // Returns the id of current Treasury Epoch.
    function get_current_epoch() public view returns (uint)
    {
        return ((block.timestamp - start_timestamp) / epoch_length);
    }

    function submit_proposal(string memory _name, string memory _url, bytes32 _hash, uint _start, uint _end, address payable _destination, uint _funding) public payable
    {   
        bytes32 name_hash = keccak256(bytes(_name));

        require(_destination != address(0x0)); // Address of a newly submitted proposal must not be 0x0.
        require(proposals[name_hash].payment_address == address(0x0).toPayable()); // Check whether a proposal exists (assuming that a proposal with address 0x0 does not exist).
        require(msg.value >= proposal_threshold);
        require(get_current_epoch() < _start);
        require(_end > _start);

        proposals[name_hash].name            = _name;
        proposals[name_hash].URL             = _url;
        proposals[name_hash].hash            = _hash;
        proposals[name_hash].start_epoch     = _start;
        proposals[name_hash].end_epoch       = _end;
        proposals[name_hash].payment_address = _destination;
        proposals[name_hash].payment_amount  = _funding;
        proposals[name_hash].status          = 0;
        proposal_names[last_proposal]        = name_hash;
        last_proposal++;
    }

    function is_votable_proposal(string memory _name) public view returns (bool)
    {
        bytes32 name_hash = keccak256(bytes(_name));
        return (proposals[name_hash].start_epoch == get_current_epoch() && proposals[name_hash].status == 0);
    }

    function cast_vote(string memory _proposal_name, uint _vote_code) public only_voter
    {
        // Vote encodings:
        // 0 - for
        // 1 - against
        // 2 - abstain

        // Check whether proposal is submitted for first voting
        // OR a multiepoch proposal is undergoing a re-evaluation.
        bytes32 proposal_name_hash = keccak256(bytes(_proposal_name));
        require(proposals[proposal_name_hash].start_epoch == get_current_epoch() ||
        (proposals[proposal_name_hash].start_epoch < get_current_epoch() && proposals[proposal_name_hash].status == 0) );

        // Check whether msg.sender has already voted on this proposal
        // clear his vote first if so.
        if(votes[proposal_name_hash][msg.sender].weight != 0)
        {
            clear_vote(_proposal_name, msg.sender);
        }

        if(_vote_code == 0)
        {
            proposals[proposal_name_hash].votes_for += voting_weight[msg.sender];
        }
        else if (_vote_code == 1)
        {
            proposals[proposal_name_hash].votes_against += voting_weight[msg.sender];
        }
        else if (_vote_code == 2)
        {
            proposals[proposal_name_hash].votes_abstain += voting_weight[msg.sender];
        }
        else
        {
            revert();
        }

        // Record voter, code and weight to prevent multiple votings from single address.
        votes[proposal_name_hash][msg.sender].weight    = voting_weight[msg.sender];
        votes[proposal_name_hash][msg.sender].vote_code = _vote_code;
        cold_staking_contract.vote_casted( msg.sender, (start_timestamp + epoch_length * get_current_epoch()) );
    }

    function evaluate_proposal(string  memory _name) public
    {
        bytes32 name_hash = keccak256(bytes(_name));
        require(proposals[name_hash].start_epoch < get_current_epoch());
        require(proposals[name_hash].end_epoch >= get_current_epoch());
        require(proposals[name_hash].status == 0);

        uint _total_votes = proposals[name_hash].votes_abstain + proposals[name_hash].votes_against + proposals[name_hash].votes_for;
        if ( _total_votes < ((total_voting_weight * voting_threshold)/100) )
        {
            // Proposal was not voted enough.
            proposals[name_hash].status = 3;
            return;
        }

        if (proposals[name_hash].votes_for > proposals[name_hash].votes_against)
        {
            // Proposal is accepted if "FOR" votes weight > "AGAINST"
            proposals[name_hash].status = 1;

            // If the proposal is accepted then send the first payment immediately.
            fund_proposal(_name);
        }
        else
        { 
            // Assign `rejected` status.
            proposals[name_hash].status = 2;
        }
    }


   function fund_proposal(string  memory _name) internal
   {
       // Checking conditions first.
       // Proposal must be `accepted` and it must not be expired.
       bytes32 name_hash = keccak256(bytes(_name));

       assert(proposals[name_hash].status == 1);
       assert(proposals[name_hash].end_epoch <= get_current_epoch());
       assert(proposals[name_hash].last_funded_epoch < get_current_epoch());

       uint epoch_delta = get_current_epoch() - proposals[name_hash].last_funded_epoch;

       proposals[name_hash].payment_address.transfer( (proposals[name_hash].payment_amount * epoch_delta) ); // Send payment * unpaid epochs count.
       proposals[name_hash].last_funded_epoch = get_current_epoch(); // Modify the last payment epoch.
   }

   function clear_vote(string  memory _proposal_name, address _voter) internal
   {
       bytes32 proposal_name_hash = keccak256(bytes(_proposal_name));
       // IMPORTANT: Voters weight could change since the moment of proposal voting.
       // `votes[proposal_name_hash][_voter].weight` is not necessarily equal to `voting_weight[_voter]`

       // Check if _voter has a record of _proposal voting.
       assert(votes[proposal_name_hash][_voter].weight != 0);

       if(votes[proposal_name_hash][_voter].vote_code == 0)
       {
           // If _voter casted a vote FOR then reduce the weight of FOR votes.
           proposals[proposal_name_hash].votes_for -= votes[proposal_name_hash][_voter].weight;
       }
       else if(votes[proposal_name_hash][_voter].vote_code == 1)
       {
           // If _voter casted a vote AGAINST then reduce the weight of AGAINST votes.
           proposals[proposal_name_hash].votes_against -= votes[proposal_name_hash][_voter].weight;
       }
       else
       {
           // Assume that _voter casted ABSTAIN vote.
           proposals[proposal_name_hash].votes_abstain -= votes[proposal_name_hash][_voter].weight;
       }

       // Zero out _voter's weight and voting record entry.
       votes[proposal_name_hash][_voter].weight    = 0;
       votes[proposal_name_hash][_voter].vote_code = 0;
   }

   // Anyone can request a funding for accepted proposal.
   function request_funding(string memory _proposal_name) public
   {
       bytes32 proposal_name_hash = keccak256(bytes(_proposal_name));
       if (proposals[proposal_name_hash].status == 0)
       {
           // If proposal has `votable` status then it must be re-evaluated. This may happen in case of multiepoch proposal.
           evaluate_proposal(_proposal_name); // Automatically funding proposal at the end of evaluation if proposal is `accepted`.
       }
       else
       {
           fund_proposal(_proposal_name); // Pay proposal if it does not require re-evaluation.
       }
   }

   function reevaluate_multiepoch_proposal(string memory _proposal_name) public only_voter
   {
       // Each voter can request a re-evaluation of already-accepted proposal if the proposal was already paid at current epoch.
       // Allows to re-evaluate each voting record manually.
       bytes32 proposal_name_hash = keccak256(bytes(_proposal_name));
       if (proposals[proposal_name_hash].last_funded_epoch == get_current_epoch())
       {
           proposals[proposal_name_hash].status = 0; // Assign `votable` status. Preserves voting records from the previous voting session.
       }
   }

    // Manually re-evaluates specified voting record.
    // This may be necessary to re-evaluate cold staker's weight
    // if it changes after the last vote record of the cold staker.
    function reevaluate_vote_record(string memory _proposal_name, address _voter) public only_voter
    {
        bytes32 proposal_name_hash = keccak256(bytes(_proposal_name));
        /*uint _delta;
        if(votes[proposal_name_hash][_voter].weight > voting_weight[_voter])
        {
            // Decrease the corresponding amount of vote records of the proposal if voter's weight decreases.
            _delta = votes[proposal_name_hash][_voter].weight - voting_weight[_voter];

            if(votes[proposal_name_hash][_voter].vote_code == 0)
            {
                proposals[proposal_name_hash].votes_for 
            }
        }*/

        require(proposals[proposal_name_hash].status == 0); // Allow re-evaluation of `votable` proposals only.
        uint _vote_code = votes[proposal_name_hash][_voter].vote_code; // Preserve the decision from the previous voting session.

        clear_vote(_proposal_name, _voter); // Clear the previous vote.

        // Cas a vote on _voter's behalf depending on previous voting session decision (FOR, AGAINST, ABSTAIN).
        if(_vote_code == 0)
        {
            proposals[proposal_name_hash].votes_for += voting_weight[_voter];
        }
        else if (_vote_code == 1)
        {
            proposals[proposal_name_hash].votes_against += voting_weight[_voter];
        }
        else if (_vote_code == 2)
        {
            proposals[proposal_name_hash].votes_abstain += voting_weight[_voter];
        }

        votes[proposal_name_hash][_voter].weight    = voting_weight[_voter];
        votes[proposal_name_hash][_voter].vote_code = _vote_code;
    }

    function withdraw_proposal(string  memory _name) public 
    {
        bytes32 name_hash = keccak256(bytes(_name));
        require(proposals[name_hash].payment_address == msg.sender.toPayable());

        proposals[name_hash].status = 3;
    }

    function is_voter(address _who) public view returns (bool) 
    {
        return voting_weight[_who] > 0;
    } 

    modifier only_staking_contract()
    {
        require(msg.sender == address(cold_staking_contract));
        _;
    }

    modifier only_voter
    {
        require(is_voter(msg.sender));
        _;
    }


    /* Commented out getter functions - prone to compiler mistakes


    // Getter functions:
    // NOTE: Solidity 0.4.25 compiler does not support enough stack depth
    // Thats why getter functions were separated into _meta_info and _votes_info getters
    // to avoid compiler stack-depth-related errors.

    function get_proposal_meta_info(string memory _name) public view returns(
                string  URL,
                bytes32 hash,
                uint    start_epoch,
                uint    end_epoch,
                address payment_address,
                uint    payment_amount,
                uint    last_funded_epoch
                )
    {
        bytes32 name_hash = keccak256(bytes(_name));
        return(
            proposals[name_hash].URL,
            proposals[name_hash].hash,
            proposals[name_hash].start_epoch,
            proposals[name_hash].end_epoch,
            proposals[name_hash].payment_address,
            proposals[name_hash].payment_amount,
            proposals[name_hash].last_funded_epoch
            );
    }

    function get_proposal_votes_info(string memory _name) public view returns(
                uint votes_for,
                uint votes_against,
                uint votes_abstain,
                uint status
                )
    {
        bytes32 name_hash = keccak256(bytes(_name));
        return(
            proposals[name_hash].votes_for,
            proposals[name_hash].votes_against,
            proposals[name_hash].votes_abstain,
            proposals[name_hash].status
            );
    }

    end of commenting getter functions */

    // DEBUGGING FUNCTIONS
    /*-------------------------------------------------------*/

    address public treasurer = msg.sender;

    modifier only_treasurer
    {
        _;
        assert(msg.sender == treasurer);
    }
    function set_staking_contract(address _new_staking_contract) public only_treasurer
    {
        cold_staking_contract = IColdStakingVoting(_new_staking_contract);
    }

    // Epoch length is set in seconds.
    function set_epoch_length(uint _epoch_length) public only_treasurer
    {
        epoch_length = _epoch_length;
    }

    function set_proposal_threshold(uint _threshold) public only_treasurer
    {
        proposal_threshold = _threshold;
    }

    // Voting threshold is set in percents.
    function set_voting_threshold(uint _threshold) public only_treasurer
    {
        voting_threshold = _threshold;
    }

    /*-------------------------------------------------------*/
    // END DEBUGGING FUNCTIONS
}
